const char *AddressToName(void *address)
{
    Dl_info dli;
    if (0 != dladdr(address, &dli))
    {
        if (dli.dli_sname != nullptr)
        {
            // C++orC
            if (*(dli.dli_sname) == '_')
            {
                int status = 0;
                return abi::__cxa_demangle(dli.dli_sname, 0, 0, &status);
            }
            else
            {
                return dli.dli_sname;
            }
        }
    }
    return nullptr;
};
